import { Box, Button, Container, FormControlLabel, Grid, TextField } from "@mui/material"
import { useEffect, useState } from "react";
import styles from 'utilities/global_styles.module.css'

interface ContactFormValues {
    name: string;
    email: string;
    message: string;
}


const ContactUsSection = () => {
    useEffect(() => {
        function hideError(e: { message: string; }) {
            if (e.message === 'ResizeObserver loop completed with undelivered notifications.') {
                const resizeObserverErrDiv = document.getElementById(
                    'webpack-dev-server-client-overlay-div'
                );
                const resizeObserverErr = document.getElementById(
                    'webpack-dev-server-client-overlay'
                );
                if (resizeObserverErr) {
                    resizeObserverErr.setAttribute('style', 'display: none');
                }
                if (resizeObserverErrDiv) {
                    resizeObserverErrDiv.setAttribute('style', 'display: none');
                }
            }
        }

        window.addEventListener('error', hideError)
        return () => {
            window.addEventListener('error', hideError)
        }
    }, [])

    const initialFormValues: ContactFormValues = {
        name: '',
        email: '',
        message: '',
    };

    const [values, setValues] = useState<ContactFormValues>(initialFormValues);

    const [errors, setErrors] = useState<ContactFormValues>({
        name: '',
        email: '',
        message: '',
    });

    const handleChange = (e: React.ChangeEvent<HTMLInputElement | HTMLTextAreaElement>) => {
        const { name, value } = e.target;
        setValues({
            ...values,
            [name]: value,
        });
    };

    const validate = (): boolean => {
        let tempErrors = { ...errors };
        tempErrors.name = values.name ? '' : 'Name is required';
        tempErrors.email = /\S+@\S+\.\S+/.test(values.email) ? '' : 'Email is not valid';
        tempErrors.message = values.message ? '' : 'Message is required';
        setErrors(tempErrors);
        return Object.values(tempErrors).every((x) => x === '');
    };

    const handleSubmit = (e: React.FormEvent) => {
        e.preventDefault();
        if (validate()) {
            console.log('Form is valid, submitting...', values);

            setValues(initialFormValues);
        } else {
            console.log('Form has errors');
        }
    };


    return <Box>
        <Grid container direction="column" paddingLeft="98px" marginTop="70px" textAlign={{xs: "center", sm: "start"}}>
            <Box className={styles.primarySubtitle}>Contact us</Box>
            <Box className={styles.highlightedProminentTitle}>Get in Touch</Box>
        </Grid>

        <Grid container marginTop="40px" position="relative" display={{ xs: "none", md: "flex" }} alignItems="center"
            style={{
                paddingLeft: "98px",
            }}>

            <Grid item md={6} textAlign="end">
                <img src="img/contact_us_illustration.svg"
                    style={{
                        maxWidth: "100%",
                        objectFit: "contain"
                    }}
                />
            </Grid>

            <Grid item md={6} direction="column" zIndex="100">
                <Box
                    component="form"
                    onSubmit={handleSubmit}
                    sx={{
                        maxWidth: '100%',
                        display: 'block',
                        padding: '20px',
                    }}
                >
                    <TextField sx={{ marginBottom: "16px" }}
                        label="Name"
                        name="name"
                        value={values.name}
                        onChange={handleChange}
                        error={!!errors.name}
                        helperText={errors.name}
                        fullWidth
                        required
                    />
                    <TextField sx={{ marginBottom: "16px" }}
                        label="Email"
                        name="email"
                        type="email"
                        value={values.email}
                        onChange={handleChange}
                        error={!!errors.email}
                        helperText={errors.email}
                        fullWidth
                        required
                    />
                    <TextField sx={{ marginBottom: "20px" }}
                        label="Message"
                        name="message"
                        value={values.message}
                        onChange={handleChange}
                        error={!!errors.message}
                        helperText={errors.message}
                        fullWidth
                        required
                        multiline
                        rows={4} // Adjust rows as needed for larger text area
                    />
                    <Box sx={{
                        width: '100%',  // Ensure the Box containing the Button is full width
                        display: 'flex',
                        justifyContent: 'center', // 
                    }}>
                        <Button className={styles.secondaryButtonPrimary} fullWidth type="submit" variant="contained" color="primary">
                            Send the message
                        </Button>
                    </Box>
                </Box>
            </Grid>




        </Grid>
        <Grid container xs={12} direction="column" marginTop="155px" alignContent="center" display={{ xs: "flex", md: "none" }} textAlign="center">
            <Box className={styles.highlightedTitle}>Follow our social media profiles and stay updated for news and exciting updates.</Box>
            <Box textAlign="center">
                <img src="img/follow_us_illustration.svg"
                    style={{
                        maxWidth: "60vw",
                        objectFit: "contain"
                    }} />
            </Box>
        </Grid>

    </Box>

}

export default ContactUsSection
