import React, { useState } from 'react';
import { Button, Dialog, DialogActions, DialogContent, DialogTitle, Typography } from '@mui/material';
import styles from 'utilities/global_styles.module.css'

// Define the component
const DownloadButton = () => {
    const [open, setOpen] = useState<boolean>(false);

    // Handle dialog open
    const handleClickOpen = () => {
        setOpen(true);
    };

    // Handle dialog close
    const handleClose = () => {
        setOpen(false);
    };

    const handleIOSClick = () => {
        console.log('Option 1 clicked');
        handleClose();
      };

      // Handle option 2 click
      const handleAndroidClick = () => {
        console.log('Option 2 clicked');
        handleClose();
      };

    return (
        <div>
            <Button
                variant="contained" className={styles.actionButtonPrimary} onClick={handleClickOpen}>Download App</Button>
            <Dialog open={open} onClose={handleClose}>
                <DialogTitle>Select a platform</DialogTitle>
                <DialogContent>
                    <Typography></Typography>
                </DialogContent>
                <DialogActions>
                    <Button onClick={handleIOSClick}>iOS</Button>
                    <Button onClick={handleAndroidClick}>Android</Button>
                </DialogActions>
            </Dialog>
        </div>
    );
};

export default DownloadButton;
