import React, { useState } from 'react';
import { Button, Dialog, DialogActions, DialogContent, DialogTitle, List, ListItem, ListItemIcon, ListItemText, Typography } from '@mui/material';
import styles from 'utilities/global_styles.module.css'

// Define the component
const DownloadButton = () => {
    const [open, setOpen] = useState<boolean>(false);

    // Handle dialog open
    const handleClickOpen = () => {
        setOpen(true);
    };

    // Handle dialog close
    const handleClose = () => {
        setOpen(false);
    };

    const handleIOSClick = () => {
        console.log('Option 1 clicked');
        handleClose();
      };

      // Handle option 2 click
      const handleAndroidClick = () => {
        console.log('Option 2 clicked');
        handleClose();
      };

    return (
        <div>
            <Button
                variant="contained" className={styles.actionButtonPrimary} onClick={handleClickOpen}>Download App</Button>
            <Dialog open={open} onClose={handleClose}>
                <DialogTitle>Select a platform</DialogTitle>
                <DialogActions>
                <List>
            <ListItem button onClick={handleIOSClick}>
              <ListItemIcon>
                {/* <HomeIcon /> */}
              </ListItemIcon>
              <ListItemText primary="Option 1" />
            </ListItem>
            <ListItem button onClick={handleAndroidClick}>
              <ListItemIcon>
                {/* <InfoIcon /> */}
              </ListItemIcon>
              <ListItemText primary="Option 2" />
            </ListItem>
          </List>
                </DialogActions>
            </Dialog>
        </div>
    );
};

export default DownloadButton;
