import React, { useState } from 'react';
import { Box, Button, Dialog, DialogActions, DialogContent, DialogTitle, List, ListItem, ListItemIcon, ListItemText, Typography } from '@mui/material';
import styles from 'utilities/global_styles.module.css'

export type FollowUsContainerProps = {
    children: any
}
// Define the component
const FollowUsContainer: React.FC<FollowUsContainerProps> = (props) => {
    const [open, setOpen] = useState<boolean>(false);

    // Handle dialog open
    const handleClickOpen = () => {
        setOpen(true);
    };

    // Handle dialog close
    const handleClose = () => {
        setOpen(false);
    };

    const handleIOSClick = () => {
        console.log('Option 1 clicked');
        handleClose();
    };

    // Handle option 2 click
    const handleAndroidClick = () => {
        console.log('Option 2 clicked');
        handleClose();
    };

    return (
        <div>
            <Box onClick={handleClickOpen}>{props.children}</Box>
            <Dialog open={open} onClose={handleClose} PaperProps={{
                sx: {
                    backgroundColor: 'orange',
                    borderRadius: "20px",
                    color: 'white',
                    padding: '20px',
                    maxWidth: '400px',
                    width: '100%',
                    boxSizing: 'border-box'
                }
            }}>
                <DialogTitle>Select a platform</DialogTitle>
                <DialogActions>
                    <List>
                        <ListItem button onClick={handleIOSClick}>
                            <ListItemIcon sx={{ width: "30px", height: "30px" }}><img src='img/apple_icon.svg' /></ListItemIcon>
                            <ListItemText primary="iOS" sx={{ color: 'white', width: '100%', justifyContent: 'center' }} />
                        </ListItem>
                        <ListItem button onClick={handleAndroidClick} sx={{ marginTop: "10px" }}>
                            <ListItemIcon sx={{ width: "30px", height: "30px" }}><img src='img/google_play_icon.svg' /></ListItemIcon>
                            <ListItemText primary="Android" sx={{ color: 'white', width: '100%', justifyContent: 'center' }} />
                        </ListItem>
                    </List>
                </DialogActions>
            </Dialog>
        </div>
    );
};

export default FollowUsContainer;
