import React, { useState } from 'react';
import { Box, Button, Dialog, DialogActions, DialogContent, DialogTitle, List, ListItem, ListItemIcon, ListItemText, Typography } from '@mui/material';
import styles from 'utilities/global_styles.module.css'

export type FollowUsContainerProps = {
    children: any
}
// Define the component
const FollowUsContainer: React.FC<FollowUsContainerProps> = (props) => {
    const [open, setOpen] = useState<boolean>(false);

    const handleClickOpen = () => {
        setOpen(true);
    };

    const handleClose = () => {
        setOpen(false);
    };

    const facbookShare = () => {
        window.open("apple.com", '_blank', "noreferrer");
        handleClose();
    };

    const instagramShare = () => {
        window.open("apple.com", '_blank');
        handleClose();
    };

    const tikTokShare = () => {
        window.open("apple.com", '_blank');
        handleClose();
    };

    return (
        <div>
            <Box onClick={handleClickOpen}>{props.children}</Box>
            <Dialog open={open} onClose={handleClose} PaperProps={{
                sx: {
                    backgroundColor: '#854AD7',
                    borderRadius: "20px",
                    color: 'white',
                    padding: '20px',
                    maxWidth: '400px',
                    width: '100%',
                    boxSizing: 'border-box'
                }
            }}>
                <DialogTitle>Follow us on</DialogTitle>
                <DialogActions>
                    <List>
                        <ListItem button onClick={facbookShare}>
                            <ListItemIcon sx={{ width: "30px", height: "30px" }}><img src='img/facebook_icon.svg' /></ListItemIcon>
                            <ListItemText primary="Facebook" sx={{ color: 'white', width: '100%', justifyContent: 'center' }} />
                        </ListItem>
                        <ListItem button onClick={instagramShare}>
                            <ListItemIcon sx={{ width: "30px", height: "30px" }}><img src='img/instagram_icon.svg' /></ListItemIcon>
                            <ListItemText primary="Instagram" sx={{ color: 'white', width: '100%', justifyContent: 'center' }} />
                        </ListItem>
                        <ListItem button onClick={tikTokShare}>
                            <ListItemIcon sx={{ width: "30px", height: "30px" }}><img src='img/tiktok_icon.svg' /></ListItemIcon>
                            <ListItemText primary="TikTok" sx={{ color: 'white', width: '100%', justifyContent: 'center' }} />
                        </ListItem>
                    </List>
                </DialogActions>
            </Dialog>
        </div>
    );
};

export default FollowUsContainer;
